@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css");

html {
  /* Theme */
  --color-accent: #716996;
  --color-shade: #e2e1ea;

  --font-heading: "Fredoka", sans-serif;
  --font-base: "Lato", sans-serif;

  /* Default property values */
  --background: #f4f3f0;
  --color: #333;
  --padding: 2rem;
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --max-width: 1250px;
  --border-color: #cbcace;
  --transition-time: 0.1s;
  --transition: var(--transition-time) color,
    var(--transition-time) background-color, var(--transition-time) border-color,
    var(--transition-time) text-decoration-color,
    var(--transition-time) box-shadow, var(--transtion-time) transform;
}

#page {
  font-family: var(--font-base);
  color: var(--color);
  line-height: 1.6;
  font-size: 1.25rem;
  font-weight: 300;
  background: var(--background);
}

.content {
  max-width: 800px;
  margin: 0 auto;
  padding: 4rem 2rem;

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  & > p {
    line-height: 1.5;
    margin-bottom: 1.5rem;
  }

  a {
  }

  h1 {
    font-family: var(--font-heading);
    font-size: 2.75rem;
    font-weight: 500;
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  h2 {
    font-family: var(--font-heading);
    font-size: 2.5rem;
    font-weight: 500;
    line-height: 1.1;
    margin-bottom: 1rem;
  }

  h3 {
    font-family: var(--font-heading);
    font-size: 2.25rem;
    font-weight: 500;
    margin-bottom: 0.5rem;
    line-height: 1.1;
  }

  ul {
    list-style: disc;
    padding-bottom: 1.5rem;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding-bottom: 1.5rem;
    padding-left: 1.25rem;
    margin-bottom: 1rem;
  }

  blockquote {
    padding: 2rem;
    margin-bottom: 1.5rem;
    border-left: 5px solid var(--color-accent);
  }
}

.section-container {
  width: 100%;
  max-width: var(--max-width, 1200px);
  margin: 0 auto;
  padding: 5rem 2rem;
}

.heading {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  line-height: 1.1;
  font-weight: 500;
  color: #333;
  @media (min-width: 800px) {
    font-size: 2.75rem;
  }
}

.button {
  color: #f6f6f9;
  font-size: 1rem;
  font-weight: 500;
  border-radius: 2rem;
  padding: 10px 24px;

  background: var(--color-accent);
  transition: var(--transition);
  white-space: nowrap;

  &:hover {
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent);
    border: 2px solid var(--color-accent);
  }
}

@keyframes pulse {
  from {
    transform: scale(0.75);
  }

  to {
    transform: scale(1);
  }
}

/* Screen-readers only */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

