aside {
  position: relative;
}

.section-container {
  /* Defined in Site CSS */
  text-align: center;
  position: relative;
  z-index: 1;
  padding: 3rem 2rem;
}

.heading {
  /* Defined in Site CSS */
  margin-bottom: 1rem;
}

.testimonial {
  display: grid;
  gap: 1.5rem;
  place-items: center;

}

.card {
  display: grid;
  gap: 1.5rem;
  place-items: center;
  padding: 2rem 1.5rem;
  width: 100%;
  max-width: 600px;

  .quote {
    font-weight: 500;
    font-size: 1.5rem;
  }

  .person {
    display: grid;
    font-size: 1rem;

    .name {
      font-weight: 400;
    }
  }
}

.controls {
  display: flex;
  justify-content: space-between;
  gap: 2rem;
  height: 90%;

  button {
    font-size: 1.5rem;
    transition: 0.1s color;

    &:hover {
      color: var(--color-accent);
    }

    &[disabled] {
      opacity: 0.25;
    }
  }
}

.dots {
  display: flex;
  justify-content: center;
  gap: 0.25rem;
  button {
    color: #d9d9d9;
    transition: 0.1s color;
    &.active {
      color: var(--color-accent);
    }
  }
}

svg {
  width: 200px;
  position: absolute;
  bottom: 0;
  left: 0;
  z-index: 0;
  translate: -50% 50%;
  animation: pulse 7s ease-in-out 0.5s infinite alternate;

  @media (prefers-reduced-motion) {
    animation: none;
  }
}

@media (min-width: 700px) {
  .testimonial {
    position: relative;
  }

  .card {
    padding: 3rem 4rem;
  }

  .controls {
    position: absolute;
    max-width: calc(600px + 6rem);
    width: 100%;
    position: absolute;
    top: 0;
  }
}

