      border: 1.5px solid var(--border-color, #eee);
      border-radius: 4px;

      &:focus {
        outline-color: var(--color-accent, rebeccapurple);
      }
    }
  }

  .button { /* Defined in Site CSS */
    margin-top: 1rem;
  }
}

* {
  --icon-size: 30px;
  --max-width: 900px;
}

.section-container {
  display: grid;
  gap: 5rem;
}

.heading-group{
  display: grid;
  gap: 1rem;
  text-align: left;
  margin-left: 0;

}
ul.cards {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(15rem, 1fr));
  gap: 5rem;

  li {
    display: grid;
    place-items: start;
    text-align: left;
    gap: 1rem;

    .icon {
      border: 1px solid var(--border-color);
      border-radius: var(--border-radius);
      padding: 0.75rem;
      color: var(--color-accent, black);
      font-size: var(--icon-size);
    }


    .title {
      color: var(--color-heading);
      display: grid;
      align-items: center;
      font-size: 1.25rem;
      line-height: 1.75;
      font-weight: 600;
    }

    .content {
      font-weight: 400;
    }
  }
}

section {
  background: var(--color-background, black);
}

.section-container {
  display: grid;
  gap: 3rem;

  color: var(--color-white);
  @media (min-width: 900px) {
    gap: 7rem;
    grid-template-columns: 1fr 1fr;
  }
}


.signup {
  display: grid;
  place-content: start;

  .heading{
    margin-bottom: 2rem;
    color: var(--color-white);
  }
  form {
    display: flex;
    gap: 0.5rem;
    margin-bottom: .75rem;

    input {
    }
    input,
    button {
      font-size: 1rem;
      padding: 0.75rem 1rem;
      border-radius: calc(var(--border-radius) / 2);
    }

    button {
      color: var(--color-white);
      border: 1px solid var(--color-white);
      font-size: 1.125rem;
      font-weight: 500;
      padding: 0.5rem 1.5rem;
    }
  }

  .footer {
    opacity: 0.7;
    font-size: 0.875rem;
    line-height: 1.75;
  }
}

.cards {
  display: grid;
  gap: 1rem;

  .card {
    display: grid;
    grid-template-columns: 3rem auto;
    column-gap: 1.5rem;
    row-gap: 0.5rem;
    padding: 1.5rem;
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);

    .icon {
      color: var(--color-brand);
      padding: 0.25rem;
      font-size: 3rem;
      grid-row: span 2;
    }

    .title {
      font-weight: 600;
      font-size: 1.125rem;
      line-height: 1.5;
    }

    .description {
      opacity: 0.7;
      font-size: 1rem;
    }
  }
}
